1import { useState } from "react";
2import "./Folder.css";
3
4const darkenColor = (hex, percent) => {
5  let color = hex.startsWith("#") ? hex.slice(1) : hex;
6  if (color.length === 3) {
7    color = color
8      .split("")
9      .map((c) => c + c)
10      .join("");
11  }
12  const num = parseInt(color, 16);
13  let r = (num >> 16) & 0xff;
14  let g = (num >> 8) & 0xff;
15  let b = num & 0xff;
16  r = Math.max(0, Math.min(255, Math.floor(r * (1 - percent))));
17  g = Math.max(0, Math.min(255, Math.floor(g * (1 - percent))));
18  b = Math.max(0, Math.min(255, Math.floor(b * (1 - percent))));
19  return (
20    "#" +
21    ((1 << 24) + (r << 16) + (g << 8) + b).toString(16).slice(1).toUpperCase()
22  );
23};
24
25const Folder = ({
26  color = "#00d8ff",
27  size = 1,
28  items = [],
29  className = "",
30}) => {
31  const maxItems = 3;
32  const papers = items.slice(0, maxItems);
33  while (papers.length < maxItems) {
34    papers.push(null);
35  }
36
37  const [open, setOpen] = useState(false);
38  const [paperOffsets, setPaperOffsets] = useState(
39    Array.from({ length: maxItems }, () => ({ x: 0, y: 0 }))
40  );
41
42  const folderBackColor = darkenColor(color, 0.08);
43  const paper1 = darkenColor("#ffffff", 0.1);
44  const paper2 = darkenColor("#ffffff", 0.05);
45  const paper3 = "#ffffff";
46
47  const handleClick = () => {
48    setOpen((prev) => !prev);
49    if (open) {
50      setPaperOffsets(Array.from({ length: maxItems }, () => ({ x: 0, y: 0 })));
51    }
52  };
53
54  const handlePaperMouseMove = (
55    e,
56    index
57  ) => {
58    if (!open) return;
59    const rect = e.currentTarget.getBoundingClientRect();
60    const centerX = rect.left + rect.width / 2;
61    const centerY = rect.top + rect.height / 2;
62    const offsetX = (e.clientX - centerX) * 0.15;
63    const offsetY = (e.clientY - centerY) * 0.15;
64    setPaperOffsets((prev) => {
65      const newOffsets = [...prev];
66      newOffsets[index] = { x: offsetX, y: offsetY };
67      return newOffsets;
68    });
69  };
70
71  const handlePaperMouseLeave = (
72    e,
73    index
74  ) => {
75    setPaperOffsets((prev) => {
76      const newOffsets = [...prev];
77      newOffsets[index] = { x: 0, y: 0 };
78      return newOffsets;
79    });
80  };
81
82  const folderStyle = {
83    "--folder-color": color,
84    "--folder-back-color": folderBackColor,
85    "--paper-1": paper1,
86    "--paper-2": paper2,
87    "--paper-3": paper3,
88  };
89
90  const folderClassName = `folder ${open ? "open" : ""}`.trim();
91  const scaleStyle = { transform: `scale(${size})` };
92
93  return (
94    <div style={scaleStyle} className={className}>
95      <div
96        className={folderClassName}
97        style={folderStyle}
98        onClick={handleClick}
99      >
100        <div className="folder__back">
101          {papers.map((item, i) => (
102            <div
103              key={i}
104              className={`paper paper-${i + 1}`}
105              onMouseMove={(e) => handlePaperMouseMove(e, i)}
106              onMouseLeave={(e) => handlePaperMouseLeave(e, i)}
107              style={
108                open
109                  ? ({
110                    "--magnet-x": `${paperOffsets[i]?.x || 0}px`,
111                    "--magnet-y": `${paperOffsets[i]?.y || 0}px`,
112                  })
113                  : {}
114              }
115            >
116              {item}
117            </div>
118          ))}
119          <div className="folder__front"></div>
120          <div className="folder__front right"></div>
121        </div>
122      </div>
123    </div>
124  );
125};
126
127export default Folder;
